mapper.select:
select * from review 
where rv_tf = #{cri.rv_tf}


	and rv_title like concat('%', #{cri.search}, '%')


	and rv_us_id = #{cri.search}


	and (rv_title like concat('%', #{cri.search}, '%') or rv_us_id = #{cri.search})



order by rv_id desc
limit #{cri.pageStart}, #{cri.perPageNum}

		select count(*) from review
		where rv_tf = #{cri.rv_tf}


      	and rv_title like concat('%', #{cri.search}, '%')


